type UniSwapV3Client struct {
	client          *ethclient.Client
	parsedABI       *abi.ABI
	contractAddress common.Address
}

func NewUniSwapV3Client(url, contractABI, contractAddress string) (*UniSwapV3Client, error) {
	client, err := ethclient.Dial(url)
	if err != nil {
		return nil, err
	}

	parsedABI, err := abi.JSON(strings.NewReader(contractABI))
	if err != nil {
		return nil, err
	}

	return &UniSwapV3Client{
		client:          client,
		parsedABI:       &parsedABI,
		contractAddress: common.HexToAddress(contractAddress),
	}, nil
}

func (c *UniSwapV3Client) GetTickData(ctx context.Context, tickSpacing *big.Int, blockNumber *big.Int) (*big.Int, error) {
	callData, err := c.parsedABI.Pack("ticks", tickSpacing)
	if err != nil {
		return nil, errors.Wrap(err, "failed to pack tick")
	}

	msg := ethereum.CallMsg{
		To:   &c.contractAddress,
		Data: callData,
	}

	tickData, err := c.client.CallContract(ctx, msg, blockNumber)
	if err != nil {
		return nil, errors.Wrap(err, "failed to call ticks")
	}

	return new(big.Int).SetBytes(tickData), nil
}

func (c *UniSwapV3Client) GetTokenBalance(ctx context.Context, tokenAddress common.Address, blockNumber *big.Int) (*big.Int, error) {
	callData, err := c.parsedABI.Pack("balanceOf", c.contractAddress)
	if err != nil {
		return nil, errors.Wrap(err, "failed to pack balanceOf")
	}

	msg := ethereum.CallMsg{
		To:   &tokenAddress,
		Data: callData,
	}

	result, err := c.client.CallContract(ctx, msg, blockNumber)
	if err != nil {
		return nil, errors.Wrap(err, "failed to call balanceOf")
	}

	var balance *big.Int
	if err = c.parsedABI.UnpackIntoInterface(&balance, "balanceOf", result); err != nil {
		return nil, errors.Wrap(err, "failed to unpack balanceOf")
	}

	return balance, nil
}
